export const isCampaign = (url: string | undefined): Promise<any> => {
    return getCatalog().then((c) => {
        c.campaigns.forEach((campaign:any) => {
            campaign.creativeSets.forEach((set:any) => {
                set.creatives.forEach((instance:any) => {
                    if(instance.payload.targetUrl == url){
                        return instance.creativeInstanceId.replace(new RegExp('-', 'g'), '')
                    }
                });
            });
            
        });
    })
} 

